  // fetches.add(fetch('http://localhost:8080/api/mobilephones'));
  // fetches.add(fetch('http://localhost:8080/api/laptops'));
  // return fetches;
  // }

  return(
    <Router>
    <Fragment>
      <Switch>
        <Route render={() => {
          return (
            <TvList tvs={inventory}/>,
            <MobilePhoneList mobilePhones={inventory}/>,
            <LaptopList laptops={inventory}/>
          )
        }}/>
      </Switch>
    </Fragment>
  </Router>
  )
}

export default InventoryContainer;

